import os
import stripe
import logging
from flask import current_app

# Configure Stripe
stripe.api_key = os.environ.get('STRIPE_SECRET_KEY')

# Get domain for redirect URLs
def get_domain():
    if os.environ.get('REPLIT_DEPLOYMENT'):
        return os.environ.get('REPLIT_DEV_DOMAIN')
    else:
        domains = os.environ.get('REPLIT_DOMAINS', 'localhost:5000')
        return domains.split(',')[0]

class StripeService:
    """Service class for handling Stripe operations"""

    # Subscription plans
    PLANS = {
        'free': {
            'name': 'Free Plan',
            'price': 0,
            'features': ['5 posters per month', 'Watermarked downloads', 'Basic templates'],
            'stripe_price_id': None
        },
        'pro': {
            'name': 'Pro Plan',
            'price': 9.99,
            'features': ['Unlimited posters', 'Watermark-free downloads', 'Premium templates', 'High-res exports'],
            'stripe_price_id': 'price_1QKoMxEm0cDh9i3v9xQzLmRr'  # Replace with your actual price ID
        },
        'premium': {
            'name': 'Premium Plan', 
            'price': 19.99,
            'features': ['Everything in Pro', 'Priority support', 'Advanced AI features', 'Custom branding'],
            'stripe_price_id': 'price_1QKoNREm0cDh9i3vYxQzLmRs'  # Replace with your actual price ID
        }
    }

    @staticmethod
    def create_checkout_session(plan_id, user_id, success_url=None, cancel_url=None):
        """Create a Stripe checkout session for subscription"""
        try:
            if plan_id not in StripeService.PLANS:
                raise ValueError(f"Invalid plan: {plan_id}")

            plan = StripeService.PLANS[plan_id]
            if not plan['stripe_price_id']:
                raise ValueError(f"No Stripe price ID for plan: {plan_id}")

            domain = get_domain()
            if not success_url:
                success_url = f"https://{domain}/payment/success"
            if not cancel_url:
                cancel_url = f"https://{domain}/payment/cancel"

            session = stripe.checkout.Session.create(
                payment_method_types=['card'],
                line_items=[{
                    'price': plan['stripe_price_id'],
                    'quantity': 1,
                }],
                mode='subscription',
                success_url=success_url + '?session_id={CHECKOUT_SESSION_ID}',
                cancel_url=cancel_url,
                metadata={
                    'user_id': str(user_id),
                    'plan_id': plan_id
                },
                automatic_tax={'enabled': True},
                billing_address_collection='required',
            )

            return session

        except Exception as e:
            logging.error(f"Error creating checkout session: {str(e)}")
            raise e

    @staticmethod
    def create_customer_portal_session(customer_id, return_url=None):
        """Create a Stripe customer portal session"""
        try:
            domain = get_domain()
            if not return_url:
                return_url = f"https://{domain}/profile"

            session = stripe.billing_portal.Session.create(
                customer=customer_id,
                return_url=return_url,
            )

            return session

        except Exception as e:
            logging.error(f"Error creating portal session: {str(e)}")
            raise e

    @staticmethod
    def get_subscription_status(customer_id):
        """Get subscription status for a customer"""
        try:
            subscriptions = stripe.Subscription.list(
                customer=customer_id,
                status='active',
                limit=1
            )

            if subscriptions.data:
                subscription = subscriptions.data[0]
                return {
                    'active': True,
                    'plan_id': subscription.metadata.get('plan_id', 'unknown'),
                    'status': subscription.status
                }
            else:
                return {'active': False}

        except Exception as e:
            logging.error(f"Error getting subscription status: {str(e)}")
            return {'active': False}

    @staticmethod
    def handle_webhook(payload, sig_header):
        """Handle Stripe webhook events"""
        try:
            # Verify webhook signature
            endpoint_secret = os.environ.get('STRIPE_WEBHOOK_SECRET')
            if endpoint_secret:
                event = stripe.Webhook.construct_event(
                    payload, sig_header, endpoint_secret
                )
            else:
                # For development, skip signature verification
                import json
                event = json.loads(payload)

            # Handle different event types
            if event['type'] == 'checkout.session.completed':
                session = event['data']['object']
                return StripeService._handle_successful_payment(session)

            elif event['type'] == 'customer.subscription.updated':
                subscription = event['data']['object']
                return StripeService._handle_subscription_update(subscription)

            elif event['type'] == 'customer.subscription.deleted':
                subscription = event['data']['object']
                return StripeService._handle_subscription_cancellation(subscription)

            return {'status': 'success'}

        except Exception as e:
            logging.error(f"Webhook error: {str(e)}")
            return {'status': 'error', 'message': str(e)}

    @staticmethod
    def _handle_successful_payment(session):
        """Handle successful payment completion"""
        try:
            from models import User, Subscription
            from app import db

            user_id = session['metadata']['user_id']
            plan_id = session['metadata']['plan_id']
            customer_id = session['customer']
            subscription_id = session['subscription']

            # Update user subscription in database
            user = User.query.get(user_id)
            if user:
                # Create or update subscription record
                subscription = Subscription.query.filter_by(user_id=user_id).first()
                if not subscription:
                    subscription = Subscription()
                    subscription.user_id = user_id
                    db.session.add(subscription)

                subscription.stripe_customer_id = customer_id
                subscription.stripe_subscription_id = subscription_id
                subscription.plan_id = plan_id
                subscription.status = 'active'

                db.session.commit()
                logging.info(f"Updated subscription for user {user_id} to {plan_id}")

            return {'status': 'success'}

        except Exception as e:
            logging.error(f"Error handling successful payment: {str(e)}")
            return {'status': 'error', 'message': str(e)}

    @staticmethod
    def _handle_subscription_update(subscription_data):
        """Handle subscription updates"""
        try:
            from models import Subscription
            from app import db

            customer_id = subscription_data['customer']
            status = subscription_data['status']

            # Find subscription by customer ID
            subscription = Subscription.query.filter_by(
                stripe_customer_id=customer_id
            ).first()

            if subscription:
                subscription.status = status
                db.session.commit()
                logging.info(f"Updated subscription status to {status}")

            return {'status': 'success'}

        except Exception as e:
            logging.error(f"Error handling subscription update: {str(e)}")
            return {'status': 'error', 'message': str(e)}

    @staticmethod
    def _handle_subscription_cancellation(subscription_data):
        """Handle subscription cancellation"""
        try:
            from models import Subscription
            from app import db

            customer_id = subscription_data['customer']

            # Find subscription by customer ID
            subscription = Subscription.query.filter_by(
                stripe_customer_id=customer_id
            ).first()

            if subscription:
                subscription.status = 'cancelled'
                subscription.plan_id = 'free'  # Downgrade to free
                db.session.commit()
                logging.info(f"Cancelled subscription for customer {customer_id}")

            return {'status': 'success'}

        except Exception as e:
            logging.error(f"Error handling subscription cancellation: {str(e)}")
            return {'status': 'error', 'message': str(e)}

static/css/style.css.content:
/* Custom CSS for Posterly */

:root {
    --primary-color: #4f46e5;
    --secondary-color: #6b7280;
    --success-color: #10b981;
    --warning-color: #f59e0b;
    --danger-color: #ef4444;
    --dark-bg: #1f2937;
    --card-bg: #374151;
}

/* General Styles */
body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    line-height: 1.6;
}

.navbar-brand {
    font-weight: 700;
    font-size: 1.5rem;
}

/* Card Enhancements */
.card {
    border: none;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    transition: all 0.3s ease;
}

.card:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
}

/* Button Enhancements */
.btn {
    border-radius: 8px;
    font-weight: 500;
    transition: all 0.2s ease;
}

.btn-primary {
    background: linear-gradient(135deg, var(--primary-color) 0%, #6366f1 100%);
    border: none;
}

.btn-primary:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(79, 70, 229, 0.4);
}

/* Form Enhancements */
.form-control {
    border-radius: 8px;
    border: 2px solid #e5e7eb;
    transition: all 0.2s ease;
}

.form-control:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 3px rgba(79, 70, 229, 0.1);
}

/* Poster Cards */
.poster-card {
    transition: all 0.3s ease;
    cursor: pointer;
    border-radius: 12px;
    overflow: hidden;
}

.poster-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
}

.poster-thumbnail {
    height: 200px;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.poster-card:hover .poster-thumbnail {
    transform: scale(1.05);
}

/* Loading States */
.spinner-border-sm {
    width: 1rem;
    height: 1rem;
}

/* Character Counter */
.character-counter {
    font-size: 0.875rem;
    margin-top: 0.25rem;
}

/* Image Preview */
.image-preview {
    margin-top: 0.5rem;
}

.image-preview img {
    border-radius: 8px;
}

/* Alert Enhancements */
.alert {
    border-radius: 8px;
    border: none;
}

.alert-info {
    background: linear-gradient(135deg, #dbeafe 0%, #bfdbfe 100%);
    color: #1e40af;
}

.alert-success {
    background: linear-gradient(135deg, #d1fae5 0%, #a7f3d0 100%);
    color: #065f46;
}

.alert-warning {
    background: linear-gradient(135deg, #fef3c7 0%, #fde68a 100%);
    color: #92400e;
}

.alert-danger {
    background: linear-gradient(135deg, #fee2e2 0%, #fecaca 100%);
    color: #991b1b;
}

/* Navigation */
.navbar {
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
}

/* Footer */
footer {
    margin-top: auto;
}

/* Responsive Images */
.img-fluid {
    border-radius: 8px;
}

/* Badge Enhancements */
.badge {
    font-weight: 500;
    border-radius: 6px;
}

/* Dropdown Enhancements */
.dropdown-menu {
    border: none;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
    border-radius: 8px;
}

.dropdown-item {
    transition: all 0.2s ease;
}

.dropdown-item:hover {
    background-color: var(--primary-color);
    color: white;
}

/* Profile Setup Form */
.profile-form .form-group {
    margin-bottom: 1.5rem;
}

/* Poster Generation Form */
.poster-form {
    background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%);
    border-radius: 12px;
    padding: 2rem;
}

/* Gallery Grid */
.gallery-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 1.5rem;
}

/* Empty State */
.empty-state {
    text-align: center;
    padding: 3rem 1rem;
}

.empty-state i {
    opacity: 0.5;
    margin-bottom: 1rem;
}

/* Animations */
@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.fade-in {
    animation: fadeIn 0.5s ease-out;
}

/* Dark Mode Specific Adjustments */
[data-bs-theme="dark"] .poster-form {
    background: linear-gradient(135deg, #374151 0%, #4b5563 100%);
}

[data-bs-theme="dark"] .card {
    background-color: var(--card-bg);
}

[data-bs-theme="dark"] .form-control {
    background-color: #4b5563;
    border-color: #6b7280;
    color: white;
}

[data-bs-theme="dark"] .form-control:focus {
    background-color: #4b5563;
    border-color: var(--primary-color);
    color: white;
}

/* Toast Container */
.toast-container {
    z-index: 1060;
}

.toast {
    border-radius: 8px;
    border: none;
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
}

/* Utility Classes */
.text-gradient {
    background: linear-gradient(135deg, var(--primary-color) 0%, #6366f1 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.shadow-custom {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
}

.shadow-custom-lg {
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
}

/* Responsive Design */
@media (max-width: 768px) {
    .poster-form {
        padding: 1rem;
    }

    .gallery-grid {
        grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
        gap: 1rem;
    }

    .navbar-brand {
        font-size: 1.25rem;
    }
}

@media (max-width: 576px) {
    .gallery-grid {
        grid-template-columns: 1fr;
    }

    .container {
        padding-left: 0.75rem;
        padding-right: 0.75rem;
    }
}

/* Print Styles */
@media print {
    .navbar,
    .footer,
    .btn,
    .alert {
        display: none !important;
    }

    .container {
        max-width: none !important;
        padding: 0 !important;
    }

    .card {
        box-shadow: none !important;
        border: 1px solid #dee2e6 !important;
    }
}

